/* Put any custom CSS styles here.  */
/* Be sure to delete the <style> tag in index.html if you want to use this stylesheet. */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  min-height: 100vh;
  background: linear-gradient(147.52deg, #f9fafb 8.89%, #d2d6db 100.48%);
}

.container {
  min-height: 100vh;
  padding: 16px;
  max-width: 1440px;
  margin: 0 auto;
}

.notfound-wrapper {
  box-shadow:
    0px 10px 15px -3px rgba(0, 0, 0, 0.1),
    0px 4px 6px -2px rgba(0, 0, 0, 0.05);
  border-radius: 6px;

  display: flex;
  flex-direction: column;
  gap: 32px;

  padding: 214px 12px;
  background: linear-gradient(360deg, rgba(255, 255, 255, 0) 0%, #ffffff 100%),
    url('https://vaqybtnqyonvlwtskzmv.supabase.co/storage/v1/object/public/projects-images/404-section/starter/img/abstract-background.jpg');
  background-position: center;
  background-size: cover;
}

.notfound__content {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.notfound__content__header {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.notfound__content__header__supporting-text {
  font-weight: 600;
  font-size: 16px;
  line-height: 24px;
  color: #4338ca;
}

.notfound__content__header h2 {
  font-weight: 600;
  font-size: 36px;
  line-height: 40px;
  color: #171717;
}

.notfound__content__message {
  font-size: 18px;
  line-height: 28px;
  color: #525252;
}

.btn {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 14px;
  height: 48px;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 4px;

  border: none;
  outline: none;
  font-family: inherit;
  font-style: normal;
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  cursor: pointer;
  width: 100%;
  white-space: noWrap;
}
.btn:focus {
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}
.btn:disabled {
  pointer-events: none;
  background: #f5f5f5;
  color: #a3a3a3;
  box-shadow: none;
}
.btn-primary {
  background: #4338ca;
  color: #ffffff;
}
.btn-primary:hover,
.btn-primary:focus {
  background: #3730a3;
}

@media screen and (min-width: 768px) {
  .notfound-wrapper {
    padding: 366px 16px;
    gap: 64px;
  }

  .notfound__content {
    gap: 24px;
  }

  .notfound__content__header {
    gap: 12px;
  }

  .notfound__content__header h2 {
    font-size: 48px;
    line-height: 48px;
  }

  .notfound__content__message {
    font-size: 20px;
    line-height: 28px;
  }

  .btn {
    height: 60px;
    font-size: 18px;
    line-height: 28px;
  }
  .notfound-btn {
    width: 173px;
  }
}

@media screen and (min-width: 1024px) {
  .notfound-wrapper {
    padding: 232px 96px;
    background: linear-gradient(
        90deg,
        #ffffff 0%,
        rgba(255, 255, 255, 0) 88.14%
      ),
      url('https://vaqybtnqyonvlwtskzmv.supabase.co/storage/v1/object/public/projects-images/404-section/starter/img/abstract-background.jpg');
    background-position: center;
    background-size: cover;
  }

  .notfound__content__header h2 {
    font-size: 60px;
    line-height: 60px;
  }
}
