/* Put any custom CSS styles here.  */
/* Be sure to delete the <style> tag in index.html if you want to use this stylesheet. */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  min-height: 100vh;
  background: linear-gradient(147.52deg, #f9fafb 8.89%, #d2d6db 100.48%);
}

.container {
  position: relative;
  min-height: 100vh;
  padding: 16px;
  max-width: 1440px;
  margin: 0 auto;
}

.toast {
  z-index: 20;
  position: absolute;
  width: 100%;
  top: 40px;
  display: none;
}

.toast__content {
  margin: 0 16px;
  display: flex;
  align-items: center;
  padding: 4px 10px 4px 4px;
  gap: 12px;

  border-radius: 2000px;

  font-size: 14px;
  line-height: 20px;
  font-weight: 500;
}

.toast__content__badge {
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 2px 10px;

  height: 24px;

  background: #ffffff;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 9999px;
}

.toast__content--error {
  color: #dc2626;
  background: #fef2f2;
}
.toast__content__badge--error {
  color: #991b1b;
}

.contact-section {
  display: grid;
  grid-template-columns: repeat(4, minmax(0, 1fr));
  gap: 32px;
  grid-row-gap: 48px;

  background: #ffffff;
  border-radius: 6px;
  box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.05);

  padding: 48px 12px;
}

.contact-section__left,
.contact-section__right {
  grid-column: span 4 / span 4;
}

.contact-section__left {
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 40px;
}

.contact-section__left__header {
  display: flex;
  flex-direction: column;
  gap: 20px;

  font-size: 18px;
  line-height: 28px;
  color: #525252;
}

.contact-section__left__header__title {
  font-weight: 600;
  font-size: 36px;
  line-height: 40px;
  color: #171717;
}

.contact-section__left__metadata {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

.contact-section__left__metadata__item {
  display: flex;
  gap: 12px;
  align-items: center;
  color: #525252;

  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
}

.contact-section__left__metadata__item_icon {
  width: 48px;
  height: 48px;
  padding: 12px;
  border-radius: 100%;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  background-color: #ffffff;
  color: #4338ca;
}

.link {
  outline: none;
  color: inherit;
  text-decoration: none;
}
.link:hover {
  color: #3730a3;
}
.link:focus {
  color: #3730a3;
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
  border-radius: 4px;
}
.link:disabled {
  pointer-events: none;
  color: #a3a3a3;
}

.contact-form {
  display: flex;
  flex-direction: column;
  gap: 40px;

  width: 100%;
  height: 100%;
  padding: 15px;
}

.contact-form,
.confirmation {
  background: #ffffff;
  border: 1px solid #e6e6e6;
  box-shadow:
    0px 10px 15px -3px rgba(0, 0, 0, 0.1),
    0px 4px 6px -2px rgba(0, 0, 0, 0.05);
  border-radius: 8px;
}

.contact-form__fields,
.contact-form__fields__name-email {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

.input {
  display: flex;
  flex-direction: column;
  gap: 6px;
  position: relative;
}

.input--width {
  flex: 1 1 0%;
}

.input__label {
  font-weight: 500;
  font-size: 14px;
  line-height: 20px;
  color: #404040;
}

.input__field {
  width: 100%;
  background: #fafafa;
  border: 1px solid #e6e6e6;
  border-radius: 4px;
  padding: 9px 13px;

  font-family: inherit;
  font-weight: 400;
  font-size: 14px;
  line-height: 20px;
  color: #171717;
  outline: none;
}

.input__field:focus {
  box-shadow:
    0px 0px 0px 1px #444ce7,
    0px 1px 2px rgba(16, 24, 40, 0.05),
    0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}

.input__field:placeholder {
  color: #737373;
}

.input__field--error {
  border: 1px solid #fca5a5;
}

.input__field--error:focus {
  box-shadow:
    0px 0px 0px 1px #d92d20,
    0px 0px 0px 4px rgba(217, 45, 32, 0.12);
}

.input__error {
  position: absolute;
  color: #d92d20;
  font-size: 14px;
  bottom: -24px;
  display: none;
}
.input__error--bottom {
  bottom: 0px;
}

.input__field--text-area {
  resize: none;
  height: 108px;
}

.input__char-count {
  display: flex;
  width: 100%;
  justify-content: flex-end;

  font-size: 14px;
  line-height: 20px;
  text-align: right;
  color: #737373;
}

.input__char-count--exceed {
  color: #d92d20;
}

.confirmation {
  display: none;
  flex-direction: column;
  height: 398px;
  align-items: center;
  justify-content: center;
  padding: 15px;
  gap: 64px;
  width: 100%;
}

.confirmation__content {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
}

.confirmation__content__icon {
  width: 64px;
  height: 64px;
  background: #ffffff;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.confirmation__content__message {
  font-size: 20px;
  line-height: 28px;
  color: #171717;
  text-align: center;
  padding: 0 14.5px;
}

.confirmation-btn {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 10px 16px;

  width: 212px;
  height: 44px;

  background: #ffffff;
  border: 0.5px solid #e6e6e6;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 4px;

  font-family: inherit;
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  color: #171717;
  cursor: pointer;
}

.btn {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 14px;
  height: 44px;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 4px;

  border: none;
  outline: none;
  font-family: inherit;
  font-style: normal;
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  cursor: pointer;
  width: 100%;
  white-space: noWrap;
}
.btn:focus {
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}
.btn:disabled {
  pointer-events: none;
  background: #f5f5f5;
  color: #a3a3a3;
  box-shadow: none;
}
.btn--primary {
  background: #4338ca;
  color: #ffffff;
}
.btn--primary:hover,
.btn--primary:focus {
  background: #3730a3;
}
.btn--secondary {
  background: #ffffff;
  border: 0.5px solid #e6e6e6;
  color: #0a0a0a;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
}
.btn--secondary:hover,
.btn--secondary:focus {
  background: #fafafa;
  border: 1px solid #e6e6e6;
}

.btn--send-message {
  width: 212px;
}

@media screen and (min-width: 768px) {
  .toast__content {
    width: max-content;
    margin: 0 auto;
  }
  .contact-section {
    padding: 83px 16px;
    box-shadow:
      0px 4px 6px -1px rgba(0, 0, 0, 0.1),
      0px 2px 4px -1px rgba(0, 0, 0, 0.06);
    border-radius: 4px;

    grid-template-columns: repeat(6, minmax(0, 1fr));
    grid-row-gap: 64px;
  }

  .contact-section__left,
  .contact-section__right {
    grid-column: span 6 / span 6;
  }

  .contact-section__left {
    gap: 48px;
  }

  .contact-section__left__header__title {
    font-size: 48px;
    line-height: 48px;
  }

  .contact-section__left__header {
    font-size: 20px;
    line-height: 28px;
  }

  .contact-form__fields__name-email {
    flex-direction: row;
    gap: 32px;
  }

  .confirmation,
  .contact-form {
    padding: 31px;
  }

  .confirmation__content__message {
    padding: 0 50px;
  }
}

@media screen and (min-width: 1024px) {
  .contact-section {
    padding: 169px 96px;
    box-shadow:
      0px 10px 15px -3px rgba(0, 0, 0, 0.1),
      0px 4px 6px -2px rgba(0, 0, 0, 0.05);
    border-radius: 6px;

    grid-template-columns: repeat(12, minmax(0, 1fr));
  }

  .contact-section__left__header__title {
    font-size: 60px;
    line-height: 60px;
  }

  .confirmation__content__message {
    padding: 0 103px;
  }
}

