@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;

  min-height: 100vh;
  background: linear-gradient(147.52deg, #f9fafb 8.89%, #d2d6db 100.48%);
}

.container {
  padding: 16px;
  max-width: 1440px;
  margin: 0 auto;
}

.faq-section {
  background: #ffffff;
  box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.05);
  border-radius: 4px;

  padding: 48px 16px;

  display: flex;
  flex-direction: column;
  gap: 48px;
}

.header {
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.header__title {
  font-weight: 600;
  font-size: 30px;
  line-height: 36px;
  text-align: center;
  color: #171717;
}

.header__subtitle {
  font-size: 18px;
  line-height: 28px;
  text-align: center;
  color: #525252;
}

.faqs {
  display: flex;
  flex-direction: column;
  gap: 28px;
}

.faq__accordion {
  display: flex;
  flex-direction: column;
  gap: 8px;

  font-size: 16px;
  line-height: 24px;
  color: #525252;
}

.faq__accordion__label {
  display: flex;
  justify-content: space-between;
  gap: 16px;

  border: none;
  outline: none;
  background: none;
  padding: 0;
  width: 100%;

  text-align: left;
  font-family: inherit;
  font-weight: 500;
  font-size: 18px;
  line-height: 28px;
  color: #171717;

  cursor: pointer;
}
.faq__accordion__label:focus {
  border-radius: 4px;
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}

.faq__accordion__toggle-icon {
  display: none;
  height: 24px;
}
.faq__accordion__toggle-icon.open {
  display: block;
}

.faq__accordion__content {
  display: none;
  padding-right: 40px;
}
.faq__accordion__content.open {
  display: block;
}

hr {
  height: 1px;
  background-color: #d4d4d4;
  border: none;
}

.faq__support__card {
  display: flex;
  flex-direction: column;
  padding: 15px;
  gap: 16px;

  background: #ffffff;
  border: 1px solid #e6e6e6;
  box-shadow:
    0px 4px 6px -1px rgba(0, 0, 0, 0.1),
    0px 2px 4px -1px rgba(0, 0, 0, 0.06);
  border-radius: 8px;
}

.faq__support__content {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.faq__support__content h6 {
  font-weight: 600;
  font-size: 20px;
  line-height: 28px;
  color: #171717;
}
.faq__support__content p {
  font-size: 16px;
  line-height: 24px;
  color: #525252;
}

.button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  font-weight: 500;
  border: none;
  background: none;
  outline: inherit;
  padding: 0;
  cursor: pointer;
  font-family: inherit;
}
.button:focus {
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}

/* Size classes */
.button--size-xl {
  height: 48px;
  column-gap: 8px;
  padding: 12px 20px;

  font-size: 16px;
  line-height: 24px;
}

/* button variants */
.button--primary {
  background: #4338ca;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  color: #ffffff;
}
.button--primary:hover,
.button--primary:focus {
  background: #3730a3;
}
.button--primary:disabled {
  background: #f5f5f5;
  box-shadow: none;
  color: #a3a3a3;
  pointer-events: none;
}

.link {
  outline: none;
  color: #4338ca;
  text-decoration: none;
}
.link:hover {
  color: #3730a3;
}
.link:focus {
  color: #3730a3;
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
  border-radius: 4px;
}
.link:disabled {
  pointer-events: none;
  color: #a3a3a3;
}

@media screen and (min-width: 768px) {
  .faq-section {
    box-shadow:
      0px 4px 6px -1px rgba(0, 0, 0, 0.1),
      0px 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 64px 16px;
    gap: 64px;
  }

  .header__title {
    font-size: 48px;
    line-height: 48px;
  }

  .header__subtitle {
    font-size: 20px;
  }

  .faq__support__card {
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    padding: 31px;
  }

  .button--faq-width {
    width: 138px;
  }
}

@media screen and (min-width: 1440px) {
  .faq-section {
    box-shadow:
      0px 10px 15px -3px rgba(0, 0, 0, 0.1),
      0px 4px 6px -2px rgba(0, 0, 0, 0.05);
    border-radius: 6px;

    padding: 96px;
  }
}

