/* Put any custom CSS styles here.  */
/* Be sure to delete the <style> tag in index.html if you want to use this stylesheet. */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  min-height: 100vh;
  background: linear-gradient(147.52deg, #f9fafb 8.89%, #d2d6db 100.48%);
}

.container {
  width: 100%;
  min-height: 100vh;
  padding: 16px;
  max-width: 1440px;
  margin: 0 auto;
}

.marquee-section {
  background: #ffffff;
  box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.05);
  border-radius: 4px;

  padding: 264px 0px;
}

.marquee-content {
  display: flex;
  flex-direction: column;
  gap: 32px;

  padding: 48px 16px;
}

.marquee-content h6 {
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  color: #525252;
}

.marquee-content__logos-wrapper {
  overflow: hidden;
}

.marquee-content__logos {
  display: flex;
  width: 200%;
}

.marquee-content__logos__group {
  display: flex;
  padding: 2px 0px;
  animation: scroll-x 30s linear infinite;
}

.marquee-content__logos__group,
.marquee-content__logos {
  gap: 78.5px;
}

@keyframes scroll-x {
  0% {
    transform: translateX(0%);
  }
  100% {
    transform: translateX(calc(-100% - 78.5px));
  }
}

.marquee-content__logos__group img {
  width: 218px;
  height: auto;
}

@media screen and (min-width: 768px) {
  .marquee-section {
    box-shadow:
      0px 4px 6px -1px rgba(0, 0, 0, 0.1),
      0px 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 356px 8px;
  }

  .marquee-content {
    padding: 64px 0px;
  }

  .marquee-content__logos__group,
  .marquee-content__logos {
    padding: 0;
    gap: 8px;
  }
}

@media screen and (min-width: 1024px) {
  .marquee-section {
    box-shadow:
      0px 10px 15px -3px rgba(0, 0, 0, 0.1),
      0px 4px 6px -2px rgba(0, 0, 0, 0.05);
    border-radius: 6px;

    padding: 196px 8px;
  }

  .marquee-content {
    padding: 96px 0px;
  }

  .marquee-content__logos__group,
  .marquee-content__logos {
    gap: 32px;
  }

  @keyframes scroll-x {
    0% {
      transform: translateX(0%);
    }
    100% {
      transform: translateX(calc(-100% - 32px));
    }
  }
}
