@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;
}

.container {
  display: flex;
  align-items: center;
  flex-direction: column;
  gap: 24px;
  margin: 0 auto;
  min-height: 100vh;
  padding: 200px 0;
}

@media screen and (min-width: 1024px) {
  .container {
    justify-content: center;
  }
}

/* Button styles */
.button {
  display: inline-flex;
  column-gap: 8px;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  font-weight: 500;
  font-size: 14px;
  line-height: 20px;
  border: none;
  background: none;
  outline: inherit;

  cursor: pointer;
  font-family: inherit;
  width: 100%;

  padding: 10px 16px;
  height: 36px;
}
.button:focus {
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}
.button:hover,
.button.active {
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px -1px rgba(0, 0, 0, 0.1);
}
.button:disabled {
  box-shadow: none;
  color: #a3a3a3;
  pointer-events: none;
}

.button--icon {
  padding: 8px 10px;
}

.pagination__container {
  padding: 16px 28px 20px 28px;
}

.pagination {
  display: flex;
}

.pagination__items {
  display: flex;
  list-style: none;
  padding: 0;
  margin: 0;
}

.pagination__item {
  padding: 8px;
  min-width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #171717;

  font-size: 14px;
  line-height: 20px;
}

