@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;

  min-height: 100vh;
  background: linear-gradient(147.52deg, #f9fafb 8.89%, #d2d6db 100.48%);
}

.container {
  min-height: 100vh;
  padding: 16px;
  max-width: 1440px;
  margin: 0 auto;
}

.team {
  background: #ffffff;
  box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.05);
  border-radius: 4px;

  padding: 48px 16px;

  display: flex;
  flex-direction: column;
  gap: 48px;
}

.header {
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.header__supporting-text {
  font-weight: 600;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  color: #4338ca;
}

.header__content {
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.header__content__title {
  font-weight: 600;
  font-size: 30px;
  line-height: 36px;
  text-align: center;
  color: #171717;
}

.header__content__subtitle {
  font-size: 20px;
  line-height: 28px;
  text-align: center;
  color: #525252;
}

.team__members {
  display: grid;
  grid-template-columns: repeat(4, minmax(0, 1fr));
  gap: 32px;
  grid-row-gap: 48px;
}

.team__members__member {
  display: flex;
  flex-direction: column;
  gap: 24px;

  grid-column: span 4 / span 4;
}

.team__members__member__picture {
  height: 296px;
  min-height: 280px;
  width: 100%;
  object-fit: cover;
}

.team__members__member__info {
  display: flex;
  flex-direction: column;
  gap: 16px;

  font-size: 16px;
  line-height: 24px;
  color: #525252;
}

.team__members__member__info__metadata {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.team__members__member__info__metadata__name {
  font-weight: 600;
  font-size: 20px;
  line-height: 28px;
  color: #171717;
}

.team__members__member__info__metadata__designation {
  font-weight: 500;
  font-size: 18px;
  line-height: 28px;
  color: #4338ca;
}

@media screen and (min-width: 768px) {
  .team {
    box-shadow:
      0px 4px 6px -1px rgba(0, 0, 0, 0.1),
      0px 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 64px 32px;
    gap: 64px;
  }

  .header__content__title {
    font-size: 48px;
    line-height: 48px;
  }

  .team__members {
    grid-template-columns: repeat(6, minmax(0, 1fr));
    padding-right: 16px;
    grid-row-gap: 64px;
  }

  .team__members__member {
    grid-column: span 3 / span 3;
  }
}

@media screen and (min-width: 1024px) {
  .team {
    box-shadow:
      0px 10px 15px -3px rgba(0, 0, 0, 0.1),
      0px 4px 6px -2px rgba(0, 0, 0, 0.05);
    border-radius: 6px;

    padding: 96px;
  }

  .team__members {
    grid-template-columns: repeat(12, minmax(0, 1fr));
    padding-right: 0;
  }
}
