/* Put any custom CSS styles here.  */
/* Be sure to delete the <style> tag in index.html if you want to use this stylesheet. */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;
}

.container {
  display: flex;
  justify-content: center;
  align-items: flex-start;
  margin: 0 auto;
  min-height: 812px;
  padding-top: 112px;
}

@media screen and (min-width: 768px) {
  .container {
    min-height: 1440px;
  }
}

@media screen and (min-width: 1440px) {
  .container {
    min-height: 768px;
  }
}

.textarea-variants {
  display: flex;
  flex-direction: column;
  gap: 32px;
  width: 340px;
}

.textarea {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.textarea label {
  font-weight: 500;
  font-size: 14px;
  line-height: 20px;
  color: #404040;
}

.textarea textarea {
  padding: 11px 13px;
  outline: none;
  border: 1px solid #e6e6e6;
  background: #fafafa;
  border-radius: 8px;

  font-family: inherit;
  font-weight: 400;
  font-size: 14px;
  line-height: 20px;
  color: #171717;
  width: 100%;

  resize: none;
  height: 108px;
}
#description-field2 {
  height: 103px;
}

.textarea textarea::placeholder {
  color: #737373;
}
.textarea textarea:focus {
  box-shadow:
    0px 0px 0px 1px #444ce7,
    0px 1px 2px rgba(16, 24, 40, 0.05),
    0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}
.textarea textarea.error {
  border: 1px solid #fca5a5;
}
.textarea textarea.error:focus {
  box-shadow:
    0px 0px 0px 1px #d92d20,
    0px 0px 0px 4px rgba(217, 45, 32, 0.12);
}

.textarea textarea:disabled::placeholder {
  color: #a3a3a3;
}
.textarea textarea:disabled {
  border: 1px solid #f5f5f5;
  color: #a3a3a3;
  pointer-events: none;
}

.textarea__message {
  font-size: 14px;
  line-height: 20px;
  color: #737373;
}

.error {
  color: #dc2626;
}

.textarea__messages {
  display: flex;
  gap: 4px;
  justify-content: space-between;
  font-size: 14px;
  line-height: 20px;
  color: #737373;
}

.textarea__messages__count {
  flex: 1;
  text-align: right;
}

