.toast.show {
  top: 20px; /* Slide in */
  opacity: 1;
}

.toast.hide {
  top: -100px; /* Slide out */
  opacity: 0;
}

.toast__content {
  width: fit-content;
  display: flex;
  align-items: center;
  padding: 4px 10px 4px 4px;
  gap: 12px;

  border-radius: 2000px;

  font-size: 14px;
  line-height: 20px;
  font-weight: 500;
}

.toast__content__badge {
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 2px 10px;

  height: 24px;

  background: #ffffff;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 9999px;
}

.toast__content--error {
  color: #dc2626;
  background: #fef2f2;
}
.toast__content__badge--error {
  color: #991b1b;
}

.toast__content--success {
  background: #f0fdf4;
  color: #15803d;
}

.toast__content--warning {
  color: #b45309;
  background: #fffbeb;
}

.toast__content--info {
  color: #525252;
  background: #f9fafb;
}

