@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;
}

.container {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 24px;
  margin: 0 auto;
  min-height: 100vh;
  padding: 200px 0;
}

.switches {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
}

.switch {
  position: relative;
  display: inline-block;
}

.switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.switch__slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  -webkit-transition: 0.4s;
  transition: 0.4s;
  background: #e5e7eb;
  border: 1px solid #e5e7eb;
  border-radius: 9999px;
}

input:checked + .switch__slider {
  background-color: #4338ca;
  border: 1px solid #4338ca;
}

.switch__slider:before {
  position: absolute;
  content: '';
  background: #ffffff;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 9999px;
  -webkit-transition: 0.4s;
  transition: 0.4s;
}

input:focus + .switch__slider {
  outline: none;
  border: 1px solid #9ca3af;
  box-shadow: 0px 0px 0px 4px rgba(157, 164, 174, 0.2);
}
input:checked:focus + .switch__slider {
  border: 1px solid #4f46e5;
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}

input:not(:disabled):hover + .switch__slider,
input:not(:disabled):focus + .switch__slider {
  background: #d1d5db;
}
input:checked:not(:disabled):hover + .switch__slider,
input:checked:not(:disabled):focus + .switch__slider {
  background: #3730a3;
}

input:disabled + .switch__slider {
  background: #f3f4f6;
  border: 1px solid #f3f4f6;
  pointer-events: none;
}

input:disabled + .switch__slider:before {
  background: #d1d5db;
}

/* Size */
.switch--size-sm {
  width: 36px;
  height: 20px;
}

.switch--size-sm .switch__slider:before {
  left: 1px;
  bottom: 1px;
  width: 16px;
  height: 16px;
}
.switch--size-sm input:checked + .switch__slider:before {
  -webkit-transform: translateX(16px);
  -ms-transform: translateX(16px);
  transform: translateX(16px);
}

.switch--size-md {
  width: 44px;
  height: 24px;
}

.switch--size-md .switch__slider:before {
  left: 1px;
  bottom: 1px;
  width: 20px;
  height: 20px;
}
.switch--size-md input:checked + .switch__slider:before {
  -webkit-transform: translateX(20px);
  -ms-transform: translateX(20px);
  transform: translateX(20px);
}
